import React, { useState, FormEvent } from 'react';
import { FiChevronRight } from 'react-icons/fi';
import api from '../../services/api';

import logoImg from '../../assets/logo.svg';

import { Title, Form, Repositories } from './styles'
import Repository from '../Repository';
import { promises } from 'dns';

interface Repository {
  full_name: string;
  description: string;
  owner : {
    login:string;
    avatar_url:string;

  }
}

const Dashboard: React.FC = () => {
  const [newRepo, setNewRepo] = useState('')
  const [repositories, setRepositories] = useState<Repository[]>([]);

  async function handleAddRepository(event: FormEvent<HTMLFormElement>): Promise<void>{
    event.preventDefault();

    const response = await api.get<Repository>(`repos/${newRepo}`);

    const repository = response.data

    setRepositories([...repositories, repository]);
  }

  return (
  <>
  <img src = {logoImg} alt="github Explorer" />
  <Title>Explore repositórios no Github.</Title>

  <Form onSubmit = {handleAddRepository}>
    <input value={newRepo} onChange = {(e) => setNewRepo(e.target.value)} placeholder = "Digite o nome do repositório"/>
    <button type="submit">Pesquisar</button>
  </Form>

  <Repositories>
    <a href = "teste">
      <img src = "https://avatars3.githubusercontent.com/u/41807432?s=460&u=297837663d284b371e92198744169fe9819f6510&v=4"
           alt= "Felipe Maia"/>
      <div>
        <strong>rocketseat/unform</strong>
        <p>Easy peasy highly scalable ReactJS & React native forms!</p>
      </div>
      <FiChevronRight size = {20}/>
    </a>

  </Repositories>
  </>
  );
};

export default Dashboard;
